$(window).on('load resize', function(){
    if ($(window).width() <= 992) {
        $('.footer-socials').insertAfter('.footer-contacts');
    } else{
        $('.footer-socials').insertAfter('.footer-subscribe');
    }
    if ($(window).width() <= 577) {
        $('.catalog-info__top').insertBefore('.catalog-item__grid');
    } else{
        $('.catalog-info__top').insertBefore('.shops');
    }
});

$("._toggle-filter").click(function () {
    $("body").toggleClass("fixed-body")
    $("body").toggleClass("_filter-opened")
})


$(".info-card__top").click(function () {
    let parent = $(this).parents(".info-card")

    if($(parent).hasClass("_active")){
        $(".info-card__bottom").slideUp()
        $(".info-card").removeClass("_active")
    } else{
        $(".info-card__bottom").slideUp()
        $(".info-card").removeClass("_active")
        $(this).siblings(".info-card__bottom").slideDown()
        $(parent).addClass("_active")
    }
})

$(".burger").click(function () {
    $(".header").toggleClass("_open-menu")
    $("body").toggleClass("fixed-body")
    $(".submenu__group").removeClass("_show")
    $(".submenu").removeClass("_show")
    $(".header").removeClass("_show-catalog")
})


function showMenuTab(tabId){
    $(".catalog-menu__category").removeClass("_active")
    $(`.catalog-menu__category[data-tab="${tabId}"]`).addClass("_active")
    $(".submenu").removeClass("_active")
    $(`.submenu[data-tab="${tabId}"]`).addClass("_active")
}

$(".catalog-menu__category").click(function () {
    let tabId = $(this).attr("data-tab")
    showMenuTab(tabId)

    if(window.innerWidth <= 992){
        $(`.submenu[data-tab="${tabId}"]`).addClass("_show")
    }
})

$(".catalog-menu__category").mouseover(function () {
    let tabId = $(this).attr("data-tab")
    if(window.innerWidth > 992){
        showMenuTab(tabId)
    }
})

